public static int findAvarage (int num1,int num2,int num3) {
	int result =num1+num2+num3/3;
	
	return result;
}


	
	
public static double findAvarage (double num1,double num2,double num3) {
	double result =num1+num2+num3/3;
	
	return result;
	
}
