package section1_3;

public class MyDate2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String today = "Friday";
		byte date = 5;
		String month= "July";
		short year = 2019;

		String eu = "EU format: \n" + "Today is " + today + ","+" "+date+"th of "+ month+", "+year;
		String us = "US format: \n" +  "Today is " + today +", " + month +" "+ date+"th,"+" " + year  ; 
		
		
		
		
		
		System.out.println(eu);
		System.out.println(us);
		
		
		
		
	}
	
	
	
	

}
