package section4_1;

public class FizzBuzz {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number=15;
		
		
		if (number % 3 == 00 && number % 5 == 0) {
			System.out.println("fizzbuzz");
		}else if (number % 3 ==0) {
			System.out.println("fizz");
		}else if (number % 5 == 0) {
			System.out.println("buzz");
		}else 
			System.out.println("");
		
		

	}

}
